def how_many_maj #Combien d'handle contient une majuscule ?
    journaliste = journalist
    i=0
    maj_cont = []
        while i < journaliste.length
        # "#{journaliste[i].count('123456789')}"
        maj_cont << "#{journaliste[i].count('ABCDEFGHIJKLMNOPQRSTUVWXYZ')}"
        i = i+1
        end
        p maj_cont
        d = maj_cont.map(&:to_i)
        p d
        d.delete(0)
        p d
        puts "il y a #{d.length} journalistes avec des majuscules dans le handle twitter"
    
end

def how_many_undersc #Combien d'underscore il y a ?
    journaliste = journalist
    i=0
    _cont = []
        while i < journaliste.length
        # "#{journaliste[i].count('123456789')}"
        _cont << "#{journaliste[i].count('_')}"
        i = i+1
        end
        p _cont
        d = _cont.map(&:to_i)
        p d
        d.delete(0)
        p d
        puts "il y a #{d.sum} journalistes avec des underscores dans le handle twitter"
    
end

def tri #trier par ordre alphabétique
